class App{init(){this.cacheDom(),this.initEvents()}cacheDom(){this.icon=document.querySelector(".chevron-down"),this.menu=document.querySelector(".dropdown-menu"),this.button=document.querySelector(".md-secondary"),this.body=document.querySelector("body")}initEvents(){this.button.addEventListener("click",(()=>{this.toggleDropDownMenu(),this.rotateIcon()}))}rotateIcon(){let t=document.querySelector(".chevron-down");var e=t.getAttribute("data-angle");e=e?parseInt(e):0,e+=180,t.style.trasform="rotate("+e+"deg)",t.setAttribute("data-angle",e)}toggleDropDownMenu(){this.body.classList.contains("show")?(this.body.classList.add("hide"),this.body.classList.remove("show")):(this.body.classList.add("show"),this.body.classList.remove("hide"))}}let a=new App;a.init();